import React, { useState } from "react";
import Navbar from "./components/Navbar";
import PinGrid from "./components/PinGrid";
import Login from "./components/Login";
import Signup from "./components/Signup";
import Upload from "./components/Upload";
import PinModal from "./components/PinModal";
import { pinsData } from "./data";
import "./App.css";

function App() {
  const [page, setPage] = useState("home");
  const [pins, setPins] = useState(pinsData);
  const [selectedPin, setSelectedPin] = useState(null);
  const [filter, setFilter] = useState("All");

  const addPin = (newPin) => {
    setPins([newPin, ...pins]);
    setPage("home");
  };

  // Filter pins based on selected category
  const filteredPins =
    filter === "All" ? pins : pins.filter((pin) => pin.category === filter);

  return (
    <div className="app">
      <Navbar setPage={setPage} setFilter={setFilter} />

      {/* HOME PAGE (all or filtered pins) */}
      {page === "home" && (
        <PinGrid pins={filteredPins} setSelectedPin={setSelectedPin} />
      )}

      {/* UPLOAD PAGE */}
      {page === "upload" && <Upload addPin={addPin} />}

      {/* LOGIN PAGE */}
      {page === "login" && <Login setPage={setPage} />}

      {/* SIGNUP PAGE */}
      {page === "signup" && <Signup setPage={setPage} />}

      {/* MODAL */}
      {selectedPin && (
        <PinModal pin={selectedPin} setSelectedPin={setSelectedPin} />
      )}
    </div>
  );
}

export default App;
