import React, { useState } from "react";
import Navbar from "./components/Navbar";
import PinGrid from "./components/PinGrid";
import Login from "./components/Login";
import Signup from "./components/Signup";
import Upload from "./components/Upload";
import PinModal from "./components/PinModal";
import { pinsData } from "./data";
import "./App.css";

function App() {
  const [page, setPage] = useState("home");
  const [pins, setPins] = useState(pinsData);
  const [selectedPin, setSelectedPin] = useState(null);

  const addPin = (newPin) => {
    setPins([newPin, ...pins]);
    setPage("home");
  };

  return (
    <div className="app">
      <Navbar setPage={setPage} />
      {page === "home" && <PinGrid pins={pins} setSelectedPin={setSelectedPin} />}
      {page === "login" && <Login setPage={setPage} />}
      {page === "signup" && <Signup setPage={setPage} />}
      {page === "upload" && <Upload addPin={addPin} />}
      {selectedPin && <PinModal pin={selectedPin} setSelectedPin={setSelectedPin} />}
    </div>
  );
}

export default App;
